    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const dispatch = useDispatch();

    const handleSubmit = (e) => {
        e.preventDefault();

        const newUser = {
            name,
            email
        };

        dispatch(addUser(newUser));

        setName('');
        setEmail('');
    };

